// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.8.0;

//      solidity   return   ABIEncoderV2          
//         js  ',' split    
// uint   int    uint  uint256 . 
//

contract subcontract {
    struct contract_info{
        string contract_info;   //  total 
        string total_cost;      //
        uint8 state;            //  0:  1:  ( wallet address ) 2:    ( wallet address ) 9:  
        address a_company;      //  
        address b_company;      // 
    }
    struct payment_list{
        uint64 contract_num;    // 
        uint8 state;            //   1:   2:  3:  
        string total_cost;      //
        uint128 ready_made_cost;//
        uint128 labor_cost;     //
        uint128 exception_cost; //
        string etc;             //  
    }

    mapping (uint64 => contract_info) internal sub_contract;    //     mapping 
    mapping (uint64 => payment_list) internal payment_state;    //      mapping 

    payment_list[] public payments;     //    


    function enroll_contract (uint64 _contract_num, string memory _contract_info, string memory _total_cost) public {   //  function
        require(sub_contract[_contract_num].state == 0);    // state 0  
        sub_contract[_contract_num].contract_info = _contract_info; //  total  sub_contract mapping contract_info 
        sub_contract[_contract_num].total_cost = _total_cost;      //  sub_contract mapping total_cost 
    }

    function sign_contract (uint64 _contract_num) public {  //   function
        address b_company = sub_contract[_contract_num].b_company;      
        address signer = msg.sender;        //msg.sender contract  
        require(b_company ==  0x0000000000000000000000000000000000000000);  //sub_contract b_company     

        sub_contract[_contract_num].b_company = signer;     //sub_contract mapping b_company contract   
        sub_contract[_contract_num].state = 1;              //sub_contract mapping state 1 
    }

    function refuse_contract (uint64 _contract_num) public { //    function
        address b_company = sub_contract[_contract_num].b_company;
        require(b_company ==  0x0000000000000000000000000000000000000000);  //sub_contract b_company     
        address refuser = msg.sender;

        sub_contract[_contract_num].b_company = refuser;    //sub_contract mapping b_company contract   
        sub_contract[_contract_num].state = 9;              //sub_contract mapping state 9 

    }

    function confirm_contract (uint64 _contract_num) public {   //        function
        address confirmer = msg.sender;

        sub_contract[_contract_num].a_company = confirmer;  //sub_contract mapping a_company contract   
        sub_contract[_contract_num].state = 2;              //sub_contract mapping state 2 
    }

    function view_contract (uint64 _contract_num) public view returns(string memory, uint8, address, address){  //mapping  data return  view function
        string memory contract_info_ = sub_contract[_contract_num].contract_info;
        uint8 state = sub_contract[_contract_num].state;
        address a_company = sub_contract[_contract_num].a_company;
        address b_company = sub_contract[_contract_num].b_company;
        return (contract_info_, state, a_company, b_company);   // _contract_num mapping data return
    }

    function request_payment (uint64 _contract_num, uint128 _ready_made_cost, uint128 _labor_cost, uint128 _exception_cost, string memory _etc) public{ //   function
        require(sub_contract[_contract_num].state == 2);    //sub_contract state 2  
        address checker = msg.sender;
        address b_company = sub_contract[_contract_num].b_company;
        string memory total_cost = sub_contract[_contract_num].total_cost;
        require(checker == b_company);      //contract  sub_contract b_company   
        payment_state[_contract_num].contract_num = _contract_num;  //   payment_state mapping 
        payment_state[_contract_num].ready_made_cost = _ready_made_cost;    //  payment_state mapping 
        payment_state[_contract_num].labor_cost = _labor_cost;  //  payment_state mapping 
        payment_state[_contract_num].exception_cost = _exception_cost;  //  payment_state mapping 
        payment_state[_contract_num].state = 1;     //   state 1 
        payment_state[_contract_num].etc = _etc;
        payments.push(payment_list(_contract_num, 1, total_cost, _ready_made_cost, _labor_cost, _exception_cost, _etc));    //    payments  
    }

    function payment (uint64 _contract_num, uint128 _ready_made_cost, uint128 _labor_cost, uint128 _exception_cost, string memory _etc) public{
        require(sub_contract[_contract_num].state == 2);
        address checker = msg.sender;
        address a_company = sub_contract[_contract_num].a_company;
        string memory total_cost = sub_contract[_contract_num].total_cost;
        require(checker == a_company);  //contract  sub_contract a_company   
        payment_state[_contract_num].state = 2;     //   state 2 
        payments.push(payment_list(_contract_num, 2, total_cost, _ready_made_cost, _labor_cost, _exception_cost, _etc));    //    payments  
    }

    function confirm_payment (uint64 _contract_num, uint128 _ready_made_cost, uint128 _labor_cost, uint128 _exception_cost, string memory _etc) public{
        require(sub_contract[_contract_num].state == 2);
        address checker = msg.sender;
        address b_company = sub_contract[_contract_num].b_company;
        string memory total_cost = sub_contract[_contract_num].total_cost;
        require(checker == b_company);      //contract  sub_contract b_company   
        payment_state[_contract_num].state = 3;     //   state 3 
        payments.push(payment_list(_contract_num, 3, total_cost, _ready_made_cost, _labor_cost, _exception_cost, _etc));    //    payments  
    }

    function refuse_payment (uint64 _contract_num, uint128 _ready_made_cost, uint128 _labor_cost, uint128 _exception_cost, string memory _etc) public{
        require(sub_contract[_contract_num].state == 2);
        address checker = msg.sender;
        address a_company = sub_contract[_contract_num].a_company;
        string memory total_cost = sub_contract[_contract_num].total_cost;
        require(checker == a_company);  //contract  sub_contract a_company   
        payment_state[_contract_num].state = 9;     //   state 9 
        payments.push(payment_list(_contract_num, 9, total_cost, _ready_made_cost, _labor_cost, _exception_cost, _etc));    //    payments  
    }

    function view_count() public view returns(uint256){     //payments   return
        return(payments.length);
    }

    function view_payment(uint _index) public view returns (uint64, uint8, string memory, uint128, uint128, uint128){       //  _index payments  return ->   .
        return(payments[_index].contract_num, payments[_index].state, payments[_index].total_cost, payments[_index].ready_made_cost, payments[_index].labor_cost, payments[_index].exception_cost);
    }

    function view_payments (uint64 _contract_num) public view returns(string memory, uint8, address, address, uint8){       //apply_list     return function
        string memory contract_info_ = sub_contract[_contract_num].contract_info;
        uint8 state = sub_contract[_contract_num].state;
        address a_company = sub_contract[_contract_num].a_company;
        address b_company = sub_contract[_contract_num].b_company;
        uint8 p_state = payment_state[_contract_num].state;
        return (contract_info_, state, a_company, b_company, p_state);
    }

    function view_payment_state(uint64 _contract_num) public view returns(string memory, uint128, uint128, uint128, uint8, string memory){  //       return  function
        string memory total_cost = payment_state[_contract_num].total_cost;
        uint128 ready_made_cost = payment_state[_contract_num].ready_made_cost;
        uint128 labor_cost = payment_state[_contract_num].labor_cost;
        uint128 exception_cost = payment_state[_contract_num].exception_cost;
        uint8 state = payment_state[_contract_num].state;
        string memory etc = payment_state[_contract_num].etc;
        return(total_cost, ready_made_cost, labor_cost, exception_cost, state, etc);
    }

}
